#include<stdio.h>//This function merges two sorted arrays.//The array is in Descending order. /*Parameterss Required:1) arr[] = The final array.2) l = The left limit or index.3) m = The middle index.4) r = The right limit or index.*/void merge(int arr[],int l,int m,int r){    int i=0,j=0,k=0;    int n1=m-l+1;//size of left array.    int n2=r-m;//size of right array.    int L[50000],R[50000];    /*    (L[]) = temporary array to store left array.    (R[]) = temporary array to store right array.    (n1) = number of elements in the left array.    (n2) = number of elements in the right array.    (i) = index variable for left array.    (j) = index variable for right array.    (k) = index variable for merged array.    */    // Storing left array in (L[]).    for(i=0;i<n1;i++)    L[i]=arr[l+i];    // Storing left array in (R[]).    for(j=0;j<n2;j++)    R[j]=arr[m+1+j];    // Reset counters.    i=j=0;// Reset to 0.    k=l;// To start of the array.    // Transfer elements from (L[]) and (R[]) to (arr[]).    while(i<n1 && j<n2)    {        if(L[i]>=R[j])// Transfer largest number first.        {            arr[k]=L[i];            i++;//increase index of (L[])        }        else        {            arr[k]=R[j];            j++;//increase index of (R[])        }        k++;//increase index of (arr[])    }    // Transfer all remaining elements from (L[])    while(i<n1)    {        arr[k]=L[i];        i++;        k++;    }    // Transfer all remaining elements from (R[])    while(j<n2)    {        arr[k]=R[j];        j++;        k++;    }}/*   This function sorts the array in descending order using MergeSort.Parameters Required:1) arr[] = The array which needs to be sorted. 2) l = The lower limit.3) r = The higher limit.*/void mergeSort(int arr[],int l,int r){    int m=0;// Middle element    if (l<r)    {        m=(l+r)/2;// The array is split in two arrays	// Funtion is called recursively to sort the left half of the array.        mergeSort(arr,l,m);	// Funtion is called recursively to sort the right half of the array.        mergeSort(arr,m+1,r);	// Calls merge function to merge the sorted left and right arrays.        merge(arr,l,m,r);    }}int main(){    // This array stores the value of days required for the Pokemon to evolve.    int a[100000];     int i=0,k=0,days=0;    /*    (i) = loop variable.    (k) = number of Pokemon.    (days) = number of days required to visit the Professor Oak.    */    // Input for Number of Pokemon. (k)    scanf("%d",&k);    // Input days required for evolution for (k) Pokemon. (a[])    for(i=0;i<k;i++)    scanf("%d",&a[i]);    // Sorts the array in descending order.    mergeSort(a,0,k-1);    // Algorithm to calculate number of days required to visit the Professor Oak.    days =1;// Set days to 1 to apply the Evolution stone on the first Pokemon.    for(i=0;i<k;i++)    {	// Checks if days needed by the current Pokemon is more than the current days needed.	if(a[i]+i+1>days)  	{	    days=a[i]+i+1;// if days needed is more change days to new value.     	}    }    days++;// Increment to account for the day of the visit.    printf("%d",days);// Displays the answer.    return 0;}